import React, { useState, useEffect } from 'react';
import './css/news.css'; 
import event1 from './images/event1.jpg';
import event2 from './images/event2.jpg';
import event3 from './images/event3.jpg';

const newsPage = () => {
  const [slideIndex, setSlideIndex] = useState(0);

  // Function to handle slide change
  const plusSlides = (n) => {
    setSlideIndex((prevIndex) => {
      let newIndex = prevIndex + n;
      if (newIndex >= slides.length) newIndex = 0;
      if (newIndex < 0) newIndex = slides.length - 1;
      return newIndex;
    });
  };

  const slides = [
    {
      img: event1,
      text: 'How Cyber Security is Evolving',
    },
    {
      img: event2,
      text: 'AI in Healthcare: A New Era',
    },
    {
      img: event3,
      text: 'Machine Learning for Beginners',
    },
  ];

  // Ensuring the first slide is visible on initial render
  useEffect(() => {
    const slides = document.querySelectorAll('.slide');
    slides.forEach((slide, index) => {
      slide.style.display = index === slideIndex ? 'block' : 'none';
    });
  }, [slideIndex]);

  return (
    <div>
      <header>
        <h1>Top News Today</h1>
      </header>

      {/* Main News Article */}
      <section className="main-article">
        <h2 className="headline">Breaking News: AI Transforms Tech Industry</h2>
        <img src="news1.jpg" alt="Main News" className="article-img" />
        <p className="article-author">By Jane Doe | Sept 5, 2024</p>
        <p className="article-content">
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </p>
      </section>

      {/* Recommended News Slider */}
      <section className="recommended-news">
        <h2>Recommended for You</h2>
        <div className="slider-container">
          <button className="prev" onClick={() => plusSlides(-1)}>&#10094;</button>
          <div className="slider">
            {slides.map((slide, index) => (
              <div className="slide" key={index} style={{ display: index === slideIndex ? 'block' : 'none' }}>
                <img src={slide.img} alt={`Recommended News ${index + 1}`} />
                <p>{slide.text}</p>
              </div>
            ))}
          </div>
          <button className="next" onClick={() => plusSlides(1)}>&#10095;</button>
        </div>
      </section>

      <footer>
        <p>&copy; 2024 News Page | Stay Informed</p>
      </footer>
    </div>
  );
};

export default newsPage;
